// Funciones en JS

const saludar = function( nombre ){
  return `Hola, ${nombre}`;
}//se aconseja manejar funciones como constantes porque es posible canbiar el valor de la funcion si no se declara como constante...

const saludar2 =()=>{
  return `Hola, ${nombre}`;
}

console.log(saludar);

// console.log(saludar("Jose"));
